// –ü–æ–ª—É—á–µ–Ω–∏–µ –∏–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ —Å–µ—Å—Å–∏–∏
if (!isset($_SESSION['user_id'])) {
    // echo "–û—à–∏–±–∫–∞: –ò–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω –≤ —Å–µ—Å—Å–∏–∏.";
    exit();
}

$user_id = $_SESSION['user_id'];
// echo "–ò–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ —Å–µ—Å—Å–∏–∏: " . $user_id . "<br>";

try {
    // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    $stmt = $db->prepare("SELECT login_time FROM users WHERE id = :user_id");
    $stmt->bindValue(':user_id', $user_id, SQLITE3_INTEGER);
    $result = $stmt->execute();

    $user = $result->fetchArray(SQLITE3_ASSOC);
    if ($user && isset($user['login_time'])) {
        $loginTime = $user['login_time'];

        // –†–∞—Å—Å—á–µ—Ç –≤—Ä–µ–º–µ–Ω–∏ –¥–æ –∫–æ–Ω—Ü–∞ –Ω–µ–¥–µ–ª–∏ (–≤ —Å–µ–∫—É–Ω–¥–∞—Ö)
        $weekInSeconds = 7 * 24 * 60 * 60; // 1 –ù–µ–¥—ñ–ª—è –≤ —Å–µ–∫—É–Ω–¥–∞—Ö
        $endTime = $loginTime + $weekInSeconds; // –í—Ä–µ–º—è –æ–∫–æ–Ω—á–∞–Ω–∏—è –Ω–µ–¥–µ–ª–∏
        $currentTime = time(); // –¢–µ–∫—É—â–µ–µ –≤—Ä–µ–º—è –≤ —Å–µ–∫—É–Ω–¥–∞—Ö —Å –Ω–∞—á–∞–ª–∞ —ç–ø–æ—Ö–∏ Unix

        // –û—Å—Ç–∞–≤—à–µ–µ—Å—è –≤—Ä–µ–º—è –¥–æ –∫–æ–Ω—Ü–∞ –Ω–µ–¥–µ–ª–∏ (–≤ —Å–µ–∫—É–Ω–¥–∞—Ö)
        $remainingTime = $endTime - $currentTime;

        // echo "–í—Ä–µ–º—è –≤–∞—à–µ–≥–æ –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ –≤—Ö–æ–¥–∞: " . date('Y-m-d H:i:s', $loginTime) . "<br>";
        // echo "–û—Å—Ç–∞–≤—à–µ–µ—Å—è –≤—Ä–µ–º—è –¥–æ –∫–æ–Ω—Ü–∞ –Ω–µ–¥–µ–ª–∏: " . gmdate("d H:i:s", $remainingTime) . "<br>";
    } else {
        // echo "–û—à–∏–±–∫–∞: –í—Ä–µ–º—è –≤—Ö–æ–¥–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ –Ω–∞–π–¥–µ–Ω–æ.";
    }
} catch (Exception $e) {
    echo '–û—à–∏–±–∫–∞: ' . $e->getMessage();
}
?>












